package com.resale.event;

import javax.servlet.ServletContextEvent;
import javax.servlet.ServletContextListener;
import javax.servlet.annotation.WebListener;

import com.resale.app.model.entity.Item;
import com.resale.app.model.entity.User;
import com.resale.database.Database;

@WebListener
public class AppInit implements ServletContextListener {

    @Override
    public void contextInitialized(ServletContextEvent sce) {
        System.out.println("*************** Initializing database *************");

        Database database = Database.getDbInstance();
        database.getUsers().add(new User(0L, "emily", "2289"));
        database.getUsers().add(new User(0L, "nasinza", "nasinza1"));

        database.getItems().add(new Item("Television", "new", 80.7,
                "data:image/jpeg;base64,"));
        database.getItems().add(new Item("Bike", "old", 700.9,
                "data:image/jpeg;base64,"));
        database.getItems().add(new Item("iphoneXR", "old", 200,
                "data:image/jpeg;base64,"));

    }

    @Override
    public void contextDestroyed(ServletContextEvent sce) {
        System.out.println();
        System.out.println();
        System.out.println();
        System.out.println("Application is undeployed or destroyed");
        System.out.println();
        System.out.println();
        System.out.println();
        System.out.println();
    }
}